(function($){Drupal.behaviors.bootstrapAlertMessages={attach:function(context){if(Drupal.settings.bootstrap.alertMessage===true){$('.block-message').each(function(i,e){var $currentMessage=$(e);$currentMessage.find('.close').click(function(e){$currentMessage.animate({opacity:0},1000,function(){$currentMessage.slideUp('slow');});e.preventDefault();});});}}};})(jQuery);